const { getDownloadDetails } = require("./index.js");

const url = "https://www.youtube.com/watch?v=dQw4w9WgXcQ"; // Replace with your video URL
const type = "mp4"; // Specify the type (e.g., mp3, mp4)
const responseType = "stream"; // Use "direct" to download, "stream" to show JSON response

async function downloadVideo() {
  try {
    const result = await getDownloadDetails(url, type, responseType);

    if (responseType === "direct") {
      console.log(`File saved at: ${result.path}`);
    } else {
      console.log("Response Data:", result);
    }
  } catch (error) {
    console.error("Error:", error.message);
  }
}

downloadVideo();